```php
function countCommonChars($str1, $str2) {
    $count = 0;
    $chars1 = str_split($str1);
    $chars2 = str_split($str2);
    foreach ($chars1 as $char1) {
        if (in_array($char1, $chars2)) {
            $count++;
            unset($chars2[array_search($char1, $chars2)]);
        }
    }
    return $count;
}
